    public function addToHelpful(Request $request, $category, $url, $reviewId)
    {
        $review = Review::find($reviewId);

        if ($review) {
            $review->like += 1;
            $review->save();

            return response()->json([
                'success' => true,
                'likes' => $review->like
            ]);
        }

        return response()->json(['success' => false], 404);
    }



